import React, { Component, useEffect, useState } from 'react';
import "../Cards.css";
import CardItem from "../CardItem";
import Auctions from './Auctions';

class homeTest extends React.Component {

	// Constructor
	constructor(props) {
		super(props);

		this.state = {
			items: [],
			DataisLoaded: false
		};
	}

	// ComponentDidMount is used to
	// execute the code
	componentDidMount() {
		fetch(
"http://localhost:7000/api/auction")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}
	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;

		return (
		<div className = "App">
			<h1>Auction</h1> {
				items.map((auction) => (
				// <ol key = { item.id } >
				// 	User_Name: { item.main_photo },
				// 	Full_Name: { item.title },
				// 	User_Email: { item.sub_titile }
				// 	</ol>
        <div>
          <CardItem
          src={auction.main_photo}
          head={auction.title}
          text={auction.sub_title}
          tag="No Reserve"
          city={auction.location}
          label="Bid Price"
          timeLeft=" 1 Day "
          bidPrice={auction.current_bid}
          path="/CarPage"
        />
        </div>
				))
			}
		</div>
	);
}
}

export default homeTest;
























