import React, { Component, useEffect, useState } from "react";
import "../Cards.css";
import CardItem from "../CardItem";
import axios from 'axios';

function auctions() {
const [posts, setposts]= useState([])

useEffect(() =>{
  axios.get('http://localhost:7000/api/auction')
  .then(res => {
    console.log(res)
    setposts(res.data)
  })
  .catch(err => {
    console.log(err)
  })
})

return (
  <>
        <div className="cards__container">
          <div className="cards__wrapper">
            <ul className="cards__items">
              <CardItem
                src="images/BMW_M4.jpg"
                head={posts.map(post => <h1 key={post.id}>{post.title}</h1>)}
                // head={this.state.apiResponse}
                text="6-Speed Manual, Texas-Kept, Unmodified"
                tag="No Reserve"
                city={auction.city}
                label="Bid Price"
                timeLeft=" 1 Day "
                bidPrice=" ₹ 17,00,000"
                path="/CarPage"
              />
            </ul>
          </div>
        </div>
    </>
)
}

export default auctions;



